package com.geeks.rvcustom;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.geeks.rvcustom.databinding.FragmentRVBinding;

import java.util.ArrayList;

public class RVFragment extends Fragment implements OnItemClick {

    FragmentRVBinding binding;
    PizzaAdapter adapter;
    ArrayList<PizzaModel> pizzaModel = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentRVBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        adapter = new PizzaAdapter(pizzaModel, this);
        binding.rvPizza.setAdapter(adapter);
    }

    private void loadData() {
    pizzaModel.add(new PizzaModel("Peperoni", "data:image/jpeg;base64,", "Peperoni good pizza", "5$"));
    pizzaModel.add(new PizzaModel("Margarita", "data:image/jpeg;base64,", "Margarita best pizza", "5$"));
    pizzaModel.add(new PizzaModel("Four cheese pizza", "data:image/jpeg;base64,", "Four cheese bad pizza", "4$"));
    pizzaModel.add(new PizzaModel("Roman pizza", "data:image/jpeg;base64,", "Roman pizza is very tasty", "3$"));
    pizzaModel.add(new PizzaModel("Pizza crudo", "data:image/jpeg;base64,", "Crudo is great", "6$"));
    pizzaModel.add(new PizzaModel("pizza americano", "data:image/jpeg;base64,", "This is really American pizza", "5$"));
 }

    @Override
    public void onClick(int position) {
        PizzaModel pizza = pizzaModel.get(position);
        Bundle bundle = new Bundle();
        bundle.putSerializable("pizza", pizza);
        DetailFragment fragment = new DetailFragment();
        fragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.ct_title, fragment).addToBackStack(null).commit();
    }
}